import React from "react";
import "../assets/styles/style_HuurEenArts.css";

class AllUsers extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [...this.props.users]
    };
  }

  debounce() {
    var button = document.getElementById("filter");
    const debounce = (func, delay) => {
      let debounceTimer;
      return function() {
        const context = this;
        const args = arguments;
        clearTimeout(debounceTimer);
        debounceTimer = setTimeout(() => func.apply(context, args), delay);
      };
    };
    button.addEventListener(
      "keyup",
      debounce(function() {
        alert(
          "Hello\nNo matter how many times you" +
            "click the debounce button, I get " +
            "executed once every 3 seconds!!"
        );
      }, 3000)
    );
  }

  handleChange = event => {
    const value = event.target.value;

    let filtered = this.props.users.filter(user => {
      let voornaam = user.voornaam.toLowerCase();
      let achternaam = user.achternaam.toLowerCase();

      if (
        voornaam.indexOf(value.toLowerCase()) !== -1 ||
        achternaam.indexOf(value.toLowerCase()) !== -1
      )
        return true;
      return false;
    });

    this.setState({
      users: filtered
    });
  };

  render() {
    return (
      <div>
        <div className="searchbar">
          <input
            type="text"
            id="filter"
            onChange={this.handleChange}
            placeholder="Zoek op naam..."
          />
        </div>

        <div>
          {this.state.users.length == 0 ? (
            <span className="notfound_text">Probeer een andere naam.</span>
          ) : (
            <div>
              {this.state.users.map((item, index) => (
                <div className="cities_card" key={index}>
                  <span className="cities_card_city">{item.voornaam}</span>
                  <span className="cities_card_country">{item.achternaam}</span>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  }
}
export default AllUsers;
