import React from "react";
import "../assets/styles/App.css";
import "../assets/styles/style_HuurEenArts.css";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { connect } from "react-redux";
import * as actionCreator from "../store/actions/actions";

class HuurEenArts extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      //   isLoading: true,
      users: [...this.props.users]
    };
  }

  componentDidMount() {
    // this.setState({
    //   isLoading: false
    // });
    console.log(this.state.users);
    this.props.getUsers();

    document.title = "Huur een arts - Huur een arts";
  }

  render() {
    if (this.props.usersIsLoading) return <div>Loading...</div>;
    return (
      <React.Fragment>
        <Header />
        <div>
          {this.state.users.length == 0 ? (
            <span className="notfound_text">No users found.</span>
          ) : (
            <div>
              {this.state.users.map((item, index) => (
                <div
                  className="user-card"
                  key={index}
                  href={
                    "/users/" + item.voornaam.replace(/\s/g, "").toLowerCase()
                  }
                >
                  <span className="cities_card_city">
                    {item.voornaam + " "}{" "}
                  </span>
                  <span className="cities_card_country">{item.achternaam}</span>
                </div>
              ))}
            </div>
          )}
        </div>
        <Footer />
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  return {
    users: state.users,
    usersIsLoading: state.usersIsLoading
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getUsers: () => dispatch(actionCreator.fetchUsers())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(HuurEenArts);
