import React from "react";
import { useFormik } from "formik";
import * as Yup from "yup";

const SignupForm = () => {
  const formik = useFormik({
    initialValues: {
      voornaam: "",
      achternaam: "",
      email: "",
    },

    validationSchema: Yup.object({
      voornaam: Yup.string()
        .max(15, "Must be 15 characters or less")
        .required("Required"),
      achternaam: Yup.string()
        .max(20, "Must be 20 characters or less")
        .required("Required"),
      email: Yup.string().email("Invalid email address").required("Required"),
    }),

    onSubmit: (values, { setSubmitting, resetForm }) => {
      fetch("/api/user/signup", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
        body: JSON.stringify(values, null, 2),
      })
        .then((res) => res.json())
        .then((json) => {
          console.log("json", json);
          if (json.success) {
            this.props.history.push("/ikbenarts/confirmation");
          }
        });
      resetForm();
      setSubmitting(false);
    },
  });

  return (
    <form encType="multipart/form-data" onSubmit={formik.handleSubmit}>
      <input
        id="voornaam"
        name="voornaam"
        type="text"
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        value={formik.values.voornaam}
      />
      {formik.touched.voornaam && formik.errors.voornaam ? (
        <div>{formik.errors.voornaam}</div>
      ) : null}

      <input
        id="achternaam"
        name="achternaam"
        type="text"
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        value={formik.values.achternaam}
      />
      {formik.touched.achternaam && formik.errors.achternaam ? (
        <div>{formik.errors.achternaam}</div>
      ) : null}

      <input
        id="email"
        name="email"
        type="email"
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        value={formik.values.email}
      />
      {formik.touched.email && formik.errors.email ? (
        <div>{formik.errors.email}</div>
      ) : null}
      <button type="submit">Submit</button>
    </form>
  );
};

export default SignupForm;
