    this.props.getUsers();

    document.title = "Huur een arts - Huur een arts";
  }

  render() {
    if (this.props.usersIsLoading) return <div>Loading...</div>;
    return (
      <React.Fragment>
        <Header />

        <AllUsers
          users={this.props.users.sort(function(a, b) {
            let userA = a.achternaam;
            let userB = b.achternaam;
            return userA < userB ? -1 : userA > userB ? 1 : 0;
          })}
        />
        {/* <div>
          {this.state.users.length === 0 ? (
            <span className="notfound_text">No users found.</span>
          ) : (
            <div>
              {this.state.users.map((item, index) => (
                <div
                  className="user-card"
                  key={index}
                  // href={
                  //   "/users/" + item.voornaam.replace(/\s/g, "").toLowerCase()
                  // }
                >
                  <span className="cities_card_city">
                    {item.voornaam + " "}
                  </span>
                  <span className="cities_card_city">
                    {item.tussenvoegsel + " "}
                  </span>
                  <span className="cities_card_country">{item.achternaam}</span>
                </div>
              ))}
            </div>
          )}
        </div> */}
        <Footer />
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  return {
    users: state.users,
    usersIsLoading: state.usersIsLoading
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getUsers: () => dispatch(actionCreator.fetchUsers())
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(HuurEenArts);
