import React from "react";
import "../assets/styles/App.css";
import "../assets/styles/style_IkBenArts.css";
import Header from "../components/Header";
import Footer from "../components/Footer";
import LoadingSpinner from "../components/LoadingSpinner";
import validate from "react-joi-validation";
import Joi from "joi-browser";

var schema = Joi.object().keys({
  voornaam: Joi.string().required(),
  achternaam: Joi.string().min(8).required(),
});

class IkBenArts2 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: true,
      geslacht: "",
      voornaam: "",
      tussenvoegsel: "",
      achternaam: "",
      geboortedatum: "",
      bigregnr: "",
      straatnaam: "",
      huisnummer: "",
      toevoeging: "",
      postcode: "",
      plaatsnaam: "",
      telefoon: "",
      email: "",
      email2: "",
      specialisme: "",
      vaardigheid: {
        BLS: false,
        ALS: false,
      },
      profilePic: null,
      errors: [],
    };
  }

  componentDidMount() {
    this.setState({
      isLoading: false,
    });

    document.title = "Ik ben arts -  Huur een arts";
  }

  loadImage = (event) => {
    let image = document.getElementById("output");
    let icon = document.getElementById("userIcon");
    this.setState({
      profilePic: event.target.files[0],
    });

    image.src = URL.createObjectURL(event.target.files[0]);
    icon.parentNode.removeChild(icon);
  };

  handleChecked = (event) => {
    let updatedCheckbox = Object.assign({}, this.state.vaardigheid, {
      [event.target.name]: event.target.checked,
    });
    this.setState({
      vaardigheid: updatedCheckbox,
    });
  };

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  // handleErrors = (event) => {
  //   let errors = this.state.errors;
  //   let name = event;
  //   if (errors.length !== 0) {
  //     errors.forEach(function (error, index) {
  //       for (var key in error) {
  //         if (error.name) console.log(error[key]);
  //         // return;
  //         // <p className="text-danger">{error[key]}</p>;
  //       }
  //     });
  //   }
  // };

  handleSubmit = (event) => {
    event.preventDefault();

    this.setState({
      isLoading: true,
    });

    const formData = new FormData();

    formData.append("geslacht", this.state.geslacht);
    formData.append("voornaam", this.state.voornaam);
    formData.append("tussenvoegsel", this.state.tussenvoegsel);
    formData.append("achternaam", this.state.achternaam);
    formData.append("geboortedatum", this.state.geboortedatum);
    formData.append("bigregnr", this.state.bigregnr);
    formData.append("straatnaam", this.state.straatnaam);
    formData.append("huisnummer", this.state.huisnummer);
    formData.append("toevoeging", this.state.toevoeging);
    formData.append("postcode", this.state.postcode);
    formData.append("plaatsnaam", this.state.plaatsnaam);
    formData.append("telefoon", this.state.telefoon);
    formData.append("email", this.state.email);
    formData.append("email2", this.state.email2);
    formData.append("BLS", this.state.vaardigheid.BLS);
    formData.append("ALS", this.state.vaardigheid.ALS);
    formData.append("specialisme", this.state.specialisme);
    formData.append("image", this.state.profilePic);

    console.log(...formData);
    console.log(this.state.vaardigheid);

    fetch("/api/user/signup", {
      method: "POST",
      body: formData,
    })
      .then((res) => res.json())
      .then((json) => {
        console.log("json", json);
        if (json.success) {
          this.setState({
            isLoading: false,
            geslacht: "",
            voornaam: "",
            tussenvoegsel: "",
            achternaam: "",
            geboortedatum: "",
            bigregnr: "",
            straatnaam: "",
            huisnummer: "",
            toevoeging: "",
            postcode: "",
            plaatsnaam: "",
            telefoon: "",
            email: "",
            email2: "",
            vaardigheid: {
              BLS: false,
              ALS: false,
            },
            specialisme: "",
            profilePic: null,
            errors: [],
          });
          this.props.history.push("/ikbenarts/confirmation");
        } else {
          this.setState({
            isLoading: false,
            errors: json.errors,
          });
        }
        console.log(this.state.errors);
      });
  };

  render() {
    if (this.state.isLoading) return <LoadingSpinner />;

    const {
      user: { voornaam, achternaam },
      errors,
      changeHandler,
      validateHandler,
    } = this.props;

    return (
      <div>
        <Header />

        <div className="heading_text">
          <span>Ik ben arts</span>
        </div>

        <div className="copy-text col-sm-12">
          <span>
            Ben je werkzaam als BIG-geregistreerd arts en heb je minimaal een
            BLS-diploma? Wil je jouw medische expertise inzetten bij foto- en
            videoshoots? Vul dan onderstaand formulier in en we zoeken een
            geschikte opdracht bij n van onze opdrachtgevers.
            <br></br>
            <br></br>
            We zijn op zoek naar artsen die achter de schermen eerste hulp
            kunnen verlenen aan o.a. modellen, acteurs of figuranten op een
            foto- of videoshoot van bijvoorbeeld TV-commercials, TV-shows of
            films.
            <br></br>
            <br></br>
            Ook zijn we op zoek naar artsen die het leuk vinden om vr de
            camera te staan om bijvoorbeeld:
            <br></br>
            <br></br>
            <ul>
              <li>
                Een medische handeling uit te voeren voor een film of commercial
              </li>
              <li>
                Medisch advies te geven over een bepaald product of dienst in
                een TV-show of commercial
              </li>
              <li>Te figureren als arts</li>
            </ul>
          </span>
        </div>

        <div className="heading_text">
          <span>Aanmeldingsformulier</span>
        </div>

        <form
          id="signup-form"
          encType="multipart/form-data"
          onSubmit={this.handleSubmit}
        >
          <div className="form_heading">
            <span>Persoonlijk</span>
          </div>

          <div>
            <input
              type="text"
              value={voornaam}
              onChange={changeHandler("voornaam")}
              onBlur={validateHandler("voornaam")}
            />

            <span> {errors.voornaam} </span>

            <input
              type="text"
              value={achternaam}
              onChange={changeHandler("achternaam")}
              onBlur={validateHandler("achternaam")}
            />

            <span> {errors.achternaam} </span>
          </div>

          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              name="geslacht"
              id="vrouw"
              value="vrouw"
              required
              checked={this.state.geslacht === "vrouw"}
              onChange={this.handleChange}
            />
            <label className="form-check-label" htmlFor="geslacht">
              Vrouw*
            </label>
          </div>
          <div className="form-check form-check-inline">
            <input
              className="form-check-input"
              type="radio"
              name="geslacht"
              id="man"
              value="man"
              checked={this.state.geslacht === "man"}
              onChange={this.handleChange}
            />
            <label className="form-check-label" htmlFor="man">
              Man*
            </label>
          </div>

          <div className="form-row">
            <div className="form-group col-sm-4">
              <input
                type="text"
                className="form-control"
                id="voornaam"
                placeholder="Voornaam*"
                name="voornaam"
                required
                onChange={this.handleChange}
              />
            </div>

            <div className="form-group col-sm-4">
              <input
                type="text"
                className="form-control"
                id="tussenvoegsel"
                placeholder="tussenvoegsel"
                name="tussenvoegsel"
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-sm-4">
              <input
                type="text"
                className="form-control"
                id="achternaam"
                placeholder="Achternaam*"
                name="achternaam"
                required
                onChange={this.handleChange}
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group col-sm-4">
              <input
                type="text"
                className="form-control"
                id="geboortedatum"
                onFocus={(e) => (e.target.type = "date")}
                placeholder="Geboortedatum*"
                name="geboortedatum"
                required
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-sm-4">
              <input
                type="number"
                className="form-control"
                id="bigreg"
                placeholder="BIG-nummer*"
                name="bigregnr"
                required
                minLength="11"
                maxLength="11"
                onChange={this.handleChange}
              />
            </div>
            <div>{this.handleErrors("bigregnr")}</div>
            {/* {this.state.errors.forEach(function (error, index) {
              for (var key in error) {
                if (error.bigregnr)
                  return <p className="text-danger">{error[key]}</p>;
              }
            })} */}
          </div>

          <div className="form_heading">
            <span>Adres</span>
          </div>

          <div className="form-row">
            <div className="form-group col-sm-4">
              <input
                type="text"
                className="form-control"
                id="straatnaam"
                placeholder="Straatnaam*"
                name="straatnaam"
                required
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-sm-4">
              <input
                type="number"
                className="form-control"
                id="huisnummer"
                placeholder="Huisnummer*"
                name="huisnummer"
                required
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-sm-4">
              <input
                type="text"
                className="form-control"
                id="toevoeging"
                placeholder="Toevoeging"
                name="toevoeging"
                onChange={this.handleChange}
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group col-sm-4">
              <input
                type="text"
                className="form-control"
                id="postcode"
                placeholder="Postcode*"
                name="postcode"
                required
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-sm-4">
              <input
                type="text"
                className="form-control"
                id="plaatsnaam"
                placeholder="Plaatsnaam*"
                name="plaatsnaam"
                required
                onChange={this.handleChange}
              />
            </div>
          </div>

          <div className="form_heading">
            <span>Contactgegevens</span>
          </div>

          <div className="form-row">
            <div className="form-group col-sm-4">
              <input
                type="number"
                className="form-control"
                id="telefoonnummer"
                placeholder="Telefoonnummer*"
                name="telefoon"
                required
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-sm-4">
              <input
                type="email"
                className="form-control"
                id="email1"
                placeholder="Emailadres*"
                name="email"
                required
                pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$"
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group col-sm-4">
              <input
                type="email"
                className="form-control"
                id="email2"
                placeholder="Emailadresverificatie*"
                name="email2"
                required
                onChange={this.handleChange}
              />
            </div>
          </div>

          <div className="form_heading">
            <span>Vaardigheden</span>
          </div>

          <div className="form-row">
            <div className="form-group col-sm-4">
              <input
                type="checkbox"
                className="form-check-input"
                id="inlineCheckbox1"
                name="BLS"
                onChange={this.handleChecked}
                value={this.state.vaardigheid["BLS"]}
              />
              <label className="form-check-label" htmlFor="inlineCheckbox1">
                Basic Life Support Diploma
              </label>
            </div>
            <div className="form-group col-sm-4">
              <input
                type="checkbox"
                className="form-check-input"
                id="inlineCheckbox2"
                name="ALS"
                onChange={this.handleChecked}
                value={this.state.vaardigheid["ALS"]}
              />
              <label className="form-check-label" htmlFor="inlineCheckbox2">
                Advanced Life Support Diploma
              </label>
            </div>

            <div className="form-group col-sm-4">
              <select
                className="custom-select"
                name="specialisme"
                required
                defaultValue={"DEFAULT"}
                onChange={this.handleChange}
              >
