import React from "react";

class ErrorList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      errors: this.props.errors,
    };
  }

  test = (error) => {
    for (var i in error) {
      console.log(error[i]);
      return <p className="error-message">{error[i]}</p>;
    }
  };

  handleErrors = () => {
    const errorList = this.props.errors;
    if (errorList.length > 0) {
      errorList.forEach(this.test);
    }
  };

  render() {
    return <div>{this.errorList > 0 && this.handleErrors()}</div>;
  }
}

export default ErrorList;
